import React from "react"
import PropTypes from "prop-types"
import '../../assets/stylesheets/Post.css'
import PostContent from "./PostContent.js";


class Post extends React.Component {

  render() {


    let imgsrc = '';

    if (!this.props.post.image.url) {
      imgsrc = '';
    }  else {
      imgsrc = this.props.post.image.url;
    }

    let postStyle = {
      backgroundImage: `url(${imgsrc})`
    };



    return (
      <div className="post">
       <div className="user_info">

       </div>
       <PostContent text = {this.props.post.content} />
       <div className="post_actions">Like Unlike {this.props.category.name}</div>
      </div>


    );
  }
}



export default Post
